import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import CloudUploadIcon from "@material-ui/icons/CloudUpload";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Link from "@material-ui/core/Link";
import Typography from "@material-ui/core/Typography";
import AddressForm from "./AddressForm";
import PaymentForm from "./PaymentForm";
import Review from "./Review";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";

const uploadButton = makeStyles(theme => ({
  button: {
    margin: theme.spacing(1)
  },
  leftIcon: {
    marginRight: theme.spacing(1)
  },
  rightIcon: {
    marginLeft: theme.spacing(1)
  },
  iconSmall: {
    fontSize: 20
  }
}));

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  appBar: {
    position: "relative"
  },
  layout: {
    width: "auto",
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 600,
      marginLeft: "auto",
      marginRight: "auto"
    }
  },
  paper: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    padding: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
      marginTop: theme.spacing(6),
      marginBottom: theme.spacing(6),
      padding: theme.spacing(3)
    }
  },
  stepper: {
    padding: theme.spacing(3, 0, 5)
  },
  buttons: {
    display: "flex",
    justifyContent: "flex-end"
  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1)
  }
}));

const steps = ["Shipping address", "Payment details", "Review your order"];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <AddressForm />;
    case 1:
      return <PaymentForm />;
    case 2:
      return <Review />;
    default:
      throw new Error("Unknown step");
  }
}

export default function UploadForm() {
  const [prodata, setProdata] = useStyles({
    CompanyName: "",
    ProjectName: "",
    ProjectDesc: "",
    City: "",
    Stat: "",
    Zip: "",
    Country: "",
    Date: "",
    Budget: ""
  });
  const classes = useStyles();
  const uButton = uploadButton();
  const [activeStep, setActiveStep] = React.useState(0);

  const handleNext = () => {
    setActiveStep(activeStep + 1);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  return (
    <React.Fragment>
      <br></br>
      <CssBaseline />
      <br></br>
      <main className={classes.layout}>
        <br></br>
        <Paper className={classes.paper}>
          <br></br>
          <br></br>
          <br></br>
          <Typography top="5" component="h1" variant="h4" align="center">
            Upload Project
          </Typography>
          <br></br>
          <br></br>
          <React.Fragment>
            <Typography variant="h5" gutterBottom>
              Project Details
            </Typography>
            <Grid container spacing={3}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="companyname"
                  name="companyname"
                  label="Company Name"
                  fullWidth
                  autoComplete="cname"
                  onClick={e => {
                    set;
                  }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="projectname"
                  name="projectname"
                  label="Project Name"
                  fullWidth
                  autoComplete="pname"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="projectdescription"
                  name="projectdescription"
                  label="Project Description Line 1"
                  fullWidth
                  autoComplete="pdescription"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  id="projectdescription2"
                  name="projectdescription2"
                  label="Project Description Line 1"
                  fullWidth
                  autoComplete="pdescription2"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="city"
                  name="city"
                  label="City"
                  fullWidth
                  autoComplete="city"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  id="state"
                  name="state"
                  label="State/Province/Region"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="zip"
                  name="zip"
                  label="Zip / Postal code"
                  fullWidth
                  autoComplete="zip"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="country"
                  name="country"
                  label="Country"
                  fullWidth
                  autoComplete="country"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="deleverydate"
                  name="deleverydate"
                  label="Delevery Date (dd / mm / yyyy)"
                  fullWidth
                  autoComplete="ddate"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  id="budget"
                  name="budget"
                  label="Budget"
                  fullWidth
                  autoComplete="budget"
                />
              </Grid>
              <Grid item xs={12}></Grid>
            </Grid>
          </React.Fragment>
          <React.Fragment>
            <Button
              variant="contained"
              color="default"
              className={uButton.button}
              type
            >
              Upload
              <CloudUploadIcon className={uButton.rightIcon} />
            </Button>
            <Button
              variant="contained"
              color="primary"
              onClick={handleNext}
              className={uButton.button}
            >
              Submit
            </Button>
          </React.Fragment>
        </Paper>
        <Copyright />
      </main>
    </React.Fragment>
  );
}
